import React,{Component} from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import StoreGrid from "../atoms/store-GridListAtom";
import AuthenticationService from '../../services/store-AuthenticationService';
import LoginMolecule from '../molecules/store-LoginMolecule';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom'
import Public from '../organisms/store-ProductOrganism';

export default function Products(){
    const [products, setProducts] = useState([]);
    let [isUserLoggedIn, setIsUserLoggedIn] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            const response = await axios.get("http://localhost:8080/products/");
            setProducts(response.data);
        };
        fetchData();
    },[]);

    async function handleOnBuy(event){
        isUserLoggedIn = AuthenticationService.isUserLoggedIn();        
        if(!isUserLoggedIn){
            console.log(isUserLoggedIn);
            return(
                <Router>
                    <div>
                        <ul>
                        <li><Link to="/">Public Page</Link></li>
                        <li><Link to="/protected">Protected Page</Link></li>
                        </ul>
                        <Route path="/" component={Public}/>
                        <Route path="/login" component={LoginMolecule}/>
                    </div>
                </Router>
            );
        }
        event.preventDefault();
        const productId =  event.target.id;
        const api = "http://localhost:8080/cart/addProduct/"+productId+"/";
        console.log(api);
        const headers = {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJzdWRoaXIiLCJleHAiOjE1OTI1NDYyNTF9.-rwl6pL7ugYbzdMyBAu28KnzqD0q_WPbDli57gUadNfj2UGHCr7-RFBeGwFPThqCzY__1VqYHv5yaudZ7c8YyQ'
          }
        const res = await axios.get(api, {
            headers: headers
          });
        if (!alert('Success!')) { window.location.reload(); }
    };


    return(        
            <div>
               <StoreGrid products ={products} handleOnBuy={handleOnBuy}/>
            </div>
    );
}
