import React from 'react';
import { BrowserRouter as Router, Route, Link, Redirect, withRouter } from 'react-router-dom';
import Public from '../components/organisms/store-ProductOrganism';
import LoginMolecule from '../components/molecules/store-LoginMolecule';
import BuyProduct from '../services/store-BuyProduct';


const fakeAuth = {
    isAuthenticated: false,
    authenticate(cb) {
      this.isAuthenticated = true
      setTimeout(cb, 100)
    },
    signout(cb) {
      this.isAuthenticated = false
      setTimeout(cb, 100)
    }
  }

const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route {...rest} render={(props) => (
      fakeAuth.isAuthenticated === true
        ? <Component {...props} />
        : <Redirect to='/login' />
    )} />
  )

  export default function Auth () {
    return (
        <Router>
            <div>
                <ul>
                <li><Link to="/">Public Page</Link></li>
                <li><Link to="/protected">Protected Page</Link></li>
                </ul>
                <Route path="/" component={Public}/>
                <Route path="/login" component={LoginMolecule}/>
                <PrivateRoute path='/protected' component={BuyProduct} />
            </div>
        </Router>
    )
  }
