import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import StoreCard from "../atoms/store-Card";
import StroeGrid from '../atoms/store-Grid'

export default function Products(){
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const response = await axios.get("http://localhost:8080/products/");
            setProducts(response.data);
        };
        fetchData();
    },[]);

    const handleOnBuy = (event) =>{
        event.preventDefault();
        const productId = event.target.id;
        const response = axios("http://localhost:8080/cart/addProduct/"+productId);
        if (!alert('New blog post has been saved!')) { window.location.reload(); }
    }


    return(        
            <div>
                <StroeGrid products={products} handleOnBuy={handleOnBuy} />
            </div>
    );
}
