import React from 'react';
import { BrowserRouter as Router, Route, Link, Redirect, withRouter } from 'react-router-dom';
import Public from '../components/organisms/store-ProductOrganism';
import LoginMolecule from '../components/molecules/store-LoginMolecule';
import BuyProduct from '../services/store-BuyProduct';


const fakeAuth = {
    isAuthenticated: false,
    authenticate(cb) {
      this.isAuthenticated = true
      setTimeout(cb, 100)
    },
    signout(cb) {
      this.isAuthenticated = false
      setTimeout(cb, 100)
    }
  }

  class Login extends React.Component{
    state = {
      redirectToReferrer: false
    }
    login = () => {
      fakeAuth.authenticate(() => {
        this.setState(() => ({
          redirectToReferrer: true
        }))
      })
    }
    render() {
      const { from } = this.props.location.state || { from: { pathname: '/' } }
      const { redirectToReferrer } = this.state

      if (redirectToReferrer === true) {
        return <Redirect to={from} />
      }

      return (
        <div>
          <p>You must log in to view the page</p>
          <button onClick={this.login}>Log in</button>
        </div>
      )
    }
  }

const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route {...rest} render={(props) => (
      fakeAuth.isAuthenticated === true
        ? <Component {...props} />
        : <Redirect to={{
          pathname: '/login',
          state: { from: props.location }
        }} />
    )} />
  )
  const AuthButton = withRouter(({ history }) => (
    fakeAuth.isAuthenticated ? (
      <p>
        Welcome! <button onClick={() => {
          fakeAuth.signout(() => history.push('/'))
        }}>Sign out</button>
      </p>
    ) : (
      <p>You are not logged in.</p>
    )
  ))

  export default function Auth () {
    console.log("In Auth");

    return (
        <Router>
            <div>
            <AuthButton/>
                <ul>
                <li><Link to="/">Public Page</Link></li>
                <li><Link to="/protected">Protected Page</Link></li>
                </ul>
                <Route path="/" component={Public}/>
                <Route path="/login" component={LoginMolecule}/>
                <PrivateRoute path='/protected' component={BuyProduct} />
            </div>
        </Router>
    )
  }
